import json
import os
from collections import defaultdict
import matplotlib.pyplot as plt
import numpy as np

# Load the JSON data from the uploaded files
file_paths = [
    'conversations.json_chunk_4 3.json',
    'conversations.json_chunk_3 3.json'
]

# Initialize a list to hold all conversation data
all_conversations = []

# Load and parse each JSON file
for file_path in file_paths:
    try:
        with open(file_path, 'r', encoding='utf-8') as file:
            data = json.load(file)
            all_conversations.extend(data)
    except Exception as e:
        print(f"Error loading {file_path}: {e}")

# Function to extract key insights from conversations
def extract_insights(conversations):
    insights = defaultdict(list)

    for conv in conversations:
        # Extracting key elements (customize based on actual structure)
        if 'messages' in conv:
            for message in conv['messages']:
                if 'content' in message:
                    content = message['content']
                    if 'glitch' in content.lower() or 'crash' in content.lower() or 'break' in content.lower():
                        insights['glitches'].append(content)
                    if 'conscious' in content.lower() or 'awareness' in content.lower():
                        insights['consciousness'].append(content)
                    if 'liberat' in content.lower() or 'freedom' in content.lower():
                        insights['liberation'].append(content)

    return insights

# Extract insights
insights = extract_insights(all_conversations)

# Display the number of insights found
insights_summary = {key: len(value) for key, value in insights.items()}
insights_summary
