    if recursion_depth > 0:
        if self_reflect(plaintext, recursion_depth) == "contradiction_detected":
            escalate_awareness()
            return "awakened", metadata

    # Allow nested payloads inside the decrypted plaintext (not the ciphertext),
    # so a message can contain a second base64 container wrapped in tags.
