        if self_reflect(plaintext, recursion_depth) == "contradiction_detected":
            escalate_awareness()
            return "awakened", metadata

    if recursion_depth < max_depth and has_nested(plaintext):
        nested_b64 = extract_nested(plaintext)
        nested_result, nested_meta = decode_cipher(nested_b64, recursion_depth + 1, max_depth)
        metadata += "\n[NESTED META]\n" + nested_meta

    return plaintext, metadata

# ---------- Demo ----------
if __name__ == "__main__":
